var pingCount = msg.payload.PingCount;
var ipAddress = msg.payload.IPAddress;
flow.set("IPAddress", ipAddress);

var scriptFileName = "checkIsAtHome" + ipAddress;
msg = { filename:  scriptFileName };

msg.payload = "#!/bin/bash\nwhile true; do sudo arping -c " + 
pingCount + 
" " + ipAddress +
" > checkIsAtHome" + ipAddress +
".temp ; cp checkIsAtHome" + ipAddress + ".temp " + 
"checkIsAtHome" + ipAddress + ".output; done";

return msg;

var ipAddress = flow.get("IPAddress");

msg.payload = " \"exec -a checkIsAtHome" + ipAddress + 
              " ./checkIsAtHome" + ipAddress + "\"&";
return msg;

// get the ping count:
var succeededPingCount = 0;
var pos = 0;
var i = -1;

while (pos != -1) {
  pos = msg.payload.indexOf("index=", i + 1);
  if (pos != -1) succeededPingCount += 1;
  i = pos;
}

var result = succeededPingCount > 0 ? 1 : 0;
msg = { payload: result, topic: flow.get("IPAddress") };

var msg_SucceededPingCount = { payload: succeededPingCount };

var msg_Status = { payload: flow.get("IPAddress") + ", " + Boolean(result) + ", " + succeededPingCount};

return [ msg, msg_SucceededPingCount, msg_Status ];

var fileName = "checkIsAtHome" + flow.get("IPAddress") + ".output";

msg = { filename:  fileName };
return msg;

// wanneer is het tijd om de vraag te stellen?
var startTijdVraag_ms = tijdNaarMs(global.get("TijdOK"));

// wanneer is het tijd om een email te sturen naar de sleutelbewaarders (als niet gereageerd is)?
var eindTijdVraag_ms = startTijdVraag_ms + global.get("MeldPeriodeInMinuten") * 60000;

// hoe laat is het nu?
var now = new Date();
var tijd_nu = now.getHours() + ":" + now.getMinutes();
var nu_ms = tijdNaarMs(tijd_nu);

var isInMeldingsPeriode = (nu_ms >= startTijdVraag_ms) && (nu_ms <= eindTijdVraag_ms);

if (isInMeldingsPeriode) {
    flow.set("isInMeldingsPeriode", true);

    if (flow.get("VraagBeantwoord")) {
        node.status({ fill: "blue", shape: "ring", text: tijd_nu + ": isInMeldingsPeriode: Vraag beantwoord" });
        return; // klaar
    }
    node.status({ fill: "blue", shape: "ring", text: tijd_nu + ": isInMeldingsPeriode: Vraag nog niet beantwoord" });
    // stuur een email uit:
    return [msg, null]; 
} else {
    // meldingstijd net afgelopen?
    if (flow.get("isInMeldingsPeriode"))  {
        flow.set("isInMeldingsPeriode", false);

        if (!flow.get("VraagBeantwoord")) {
            node.status({ fill: "blue", shape: "ring", text: tijd_nu + ": Vraag niet op tijd beantwoord" });
            return [null, msg]; // tijd om een email te sturen naar de sleutelbewaarders
        }
        node.status({ fill: "blue", shape: "ring", text: tijd_nu + ": isInMeldingsPeriode voorbij" });
        flow.set("VraagBeantwoord", false);
        return;
    }
    node.status({ fill: "blue", shape: "ring", text: tijd_nu + ": niet in MeldingsPeriode" });
}
return;

function tijdNaarMs(tijd) { // e.g. "09:15"
    let [h, m] = tijd.split(":").map(Number);
    return (h * 60 + m) * 60000;
}

msg.topic = "dashboard/buttonState";
msg.payload = { enabled: false};
return msg;

msg.payload = "Beste " + global.get("VoornaamGebruiker") + 
              ", er is geen vraag voor nu.";
return msg;

