var tijd_nu = now.getHours() + ":" + now.getMinutes();

var isSystemOn = global.get("SystemOn");
if (!isSystemOn) {
    node.status({ fill: "blue", shape: "ring", text: tijd_nu + ": System is Off" });
    return;
}

var isThuis = global.get("GebruikerIsAtHome");
var isThuisString = isThuis ? ": is thuis" : ": niet thuis";
node.status({ fill: "blue", shape: "ring", 
    text: tijd_nu + isThuisString
    });

if (isThuis)
    return msg;

