var tijd_nu = now.getHours() + ":" + now.getMinutes();
node.status({ fill: "blue", shape: "ring", text: "Send: " + tijd_nu });
return msg;

var text = msg.payload;

msg.payload = {
    to: global.get("EmailSleutelbewaarder1"),
    cc: global.get("EmailSleutelbewaarder2"),
    from: "safeathomealone@gmail.com",
    topic: msg.topic,
