var msg = { enabled: buttonEnabled };
return msg;

var bericht = "Systeem uitgeschakeld.";
if (msg.payload) bericht = "Systeem ingeschakeld.";

msg.payload = bericht;
return msg;

// verwijder spaties
let origineleString = msg.payload;
if (origineleString == undefined) return;
let zonderSpaties = origineleString.replace(/\s+/g, "");
msg.payload = zonderSpaties;

return msg;

