var tijd_nu = global.get("getTime")();
let currentState = global.get("currentState");
let previousState = currentState;
global.set("previousState", previousState);

let trigger = msg.payload;

switch (currentState) {
    case states.IDLE: {
        if (trigger == "beginVraagTijdVoorbij") currentState = states.TOONOKVRAAG;
        if (trigger == "meldingstijdGebruikerVoorbij") currentState = states.MELDGEBRUIKER;
        break;
    }
    case states.TOONOKVRAAG: {
        if (trigger == "middernacht") currentState = states.IDLE; 
        if (trigger == "jaPressed") currentState = states.ISOK;
        if (trigger == "neePressed") currentState = states.ISNOTOK;
        if (trigger == "meldingstijdGebruikerVoorbij") currentState = states.MELDGEBRUIKER;
        if (trigger == "eindVraagTijdVoorbij") currentState = states.OVERTIJD;
        break;
    }
    case states.MELDGEBRUIKER: {
        if (trigger == "middernacht") currentState = states.IDLE; 
        if (trigger == "jaPressed") currentState = states.ISOK;
        if (trigger == "neePressed") currentState = states.ISNOTOK;
        if (trigger == "eindVraagTijdVoorbij") currentState = states.OVERTIJD;
        break;
    }
    case states.ISOK: {
        if (trigger == "middernacht") currentState = states.IDLE; 
        break;
    }
    case states.ISNOTOK: {
        if (trigger == "middernacht") currentState = states.IDLE; 
        break;
    }
    case states.ISOK_OVERTIJD: {
        if (trigger == "middernacht") currentState = states.IDLE; 
        break;
    }
    case states.ISNOTOK_OVERTIJD: {
        if (trigger == "middernacht") currentState = states.IDLE; 
        break;
    }
    case states.OVERTIJD: {
        if (trigger == "middernacht") currentState = states.IDLE; 
        if (trigger == "jaPressed") currentState = states.ISOK_OVERTIJD;
        if (trigger == "neePressed") currentState = states.ISNOTOK_OVERTIJD;
        break;
    }
}

global.set("currentState", currentState);

node.status({ text: tijd_nu + " State: " + currentState });

if (currentState != previousState) {
    msg.payload = currentState;
    return msg;
}
else
    return;

var tijd_nu = global.get("getTime")();
let currentState = global.get("currentState");
let previousState = global.get("previousState");

let states = global.get("states");
switch (currentState) {
    case states.IDLE: {
        msg.reset = true;
        node.status({ text: tijd_nu + ", IDLE" });
        return [msg, null, null, null];
    }
    case states.TOONOKVRAAG: {
        node.status({ text: tijd_nu + ", TOONOKVRAAG" });
        break;
    }
    case states.MELDGEBRUIKER: {
        msg.delay = global.get("Herinnering") * 60000;
        node.status({ text: tijd_nu + ", MELDGEBRUIKER, delay (min): " + global.get("Herinnering") });
        return [msg, null, null, null];
    }
    case states.ISOK: {
        msg.reset = true;
        node.status({ text: tijd_nu + ", reset timer, ISOK" });
        return [msg, null, null, null];
    }
    case states.ISNOTOK: {
        msg.reset = true;
        node.status({ text: tijd_nu + ", ISNOTOK" });
        return [msg, null, msg, null];
    }
    case states.ISOK_OVERTIJD: {
        msg.reset = true;
        msg.payload = "JA";
        if (currentState != previousState)
            node.status({ text: tijd_nu + ", ISOK_OVERTIJD" });
            return [msg, null, null, msg];
        break;
    }
    case states.ISNOTOK_OVERTIJD: {
        msg.reset = true;
        msg.payload = "NEE";
        if (currentState != previousState)
            node.status({ text: tijd_nu + ", ISNOTOK_OVERTIJD" });
            return [msg, null, null, msg];
        break;
    }
    case states.OVERTIJD: {
        if (currentState != previousState) {
            node.status({ text: tijd_nu + ", OVERTIJD" });
            return [null, msg, null, null];
        }
    }
}
return;

