function stripAnsi(str) {
    return str.replace(/\x1B\[[0-9;]*[A-Za-z]/g, '');
}

let devices = msg.devices;
if (Array.isArray(devices) && devices.length === 0) {
    node.status({ text: "geen devices gekoppeld" });
    msg.payload = "bluetoothctl quit";    
    return msg;
}

let cmd = "";
for (const device of devices) {
    cmd += "bluetoothctl info " + device.mac + ";";
}
msg.payload = cmd;
node.status({ text: "bluetoothctl info ..." });

return msg;

let devices = [];

if (msg.payload == "") {
    node.status({ text: "payload empty" });
    msg.payload = devices;
    return msg;
}

const text = msg.payload;

const blocks = text.split(/(?=Device\s+[0-9A-F:]{17})/gi);

for (const block of blocks) {
    // MAC-adres
    const macMatch = block.match(/Device\s+([0-9A-F:]{17})/i);
    if (!macMatch) continue;

    // Naam
    const nameMatch = block.match(/Name:\s*(.+)/i);
    const name = nameMatch ? nameMatch[1].trim() : null;

    // Connected-status
    const connMatch = block.match(/Connected:\s*(yes|no)/i);
    const connected = connMatch ? connMatch[1].toLowerCase() : null;

    devices.push({
        mac: macMatch[1],
        name,
        connected
    });
}

msg.payload = devices;
return msg;

